import { Component } from '@angular/core';


@Component({
  selector: 'app-preferences',
  templateUrl: './preferences.component.html',
  styleUrls: ['./preferences.component.css']
})
export class PreferencesComponent {
  majors: Array<Major> = [
    {id: 1,name: 'Computer Science'},
    {id: 2, name: 'Math'}
  ]

  lifestyles: Array<Lifestyle> = [
    {name: 'Outdoorsey'},
    {name: 'Nightlife'},
    {name: 'Bookworm'}
  ]

  selectedMajor = this.majors[0];
  selectedLifestyle = this.lifestyles[0];
}

interface Major {
  name: string,
  id: number
}

interface Lifestyle {
  name: string
}
<h2>Preferences</h2>
<div class="prefGroup">
  <label for="majors">Major: </label>
  <select id="majors" [(ngModel)]="selectedMajor">
    <option *ngFor="let m of majors" [ngValue]="m">{{m.name}}</option>
  </select>
</div>

<div class="prefGroup">
  <label for="lifestyles">Lifestyle:</label>
  <select id="lifestyles" [(ngModel)]="selectedLifestyle">
    <option *ngFor="let l of lifestyles" [ngValue]="l">{{l.name}}</option>
  </select>
</div>
